// Variables Globals.
var Mapa = {};
var Idioma = {};
var IdIdioma = "";

// Canviam els diferents literals de la GUI segons l'idioma
function CanviarIdioma(IdMapa, IdIdioma_) {
    // window.alert(all_maps.map1.ca[1]);
    // Finestra(all_maps.map1.ca[1]);
    
    IdIdioma = IdIdioma_;
    document.getElementById("bandera").src = "img/" + IdIdioma + ".png";      

    switch(IdMapa) {
      case "map1":  Mapa = all_maps.map1;  break;
      case "map2":  Mapa = all_maps.map2;  break;
      case "map3":  Mapa = all_maps.map3;  break;
      case "map4":  Mapa = all_maps.map4;  break;
      case "map5":  Mapa = all_maps.map5;  break;
      case "map6":  Mapa = all_maps.map6;  break;
      case "map7":  Mapa = all_maps.map7;  break;
      case "map8":  Mapa = all_maps.map8;  break;
      case "map9":  Mapa = all_maps.map9;  break;
      case "map10": Mapa = all_maps.map10; break;
      case "map11": Mapa = all_maps.map11; break;
      case "map12": Mapa = all_maps.map12; break;
    }

    switch(IdIdioma) {
      case "la": Idioma = Mapa.la; break;
      case "ca": Idioma = Mapa.ca; break;
      case "en": Idioma = Mapa.en; break;
      case "es": Idioma = Mapa.es; break;
      case "de": Idioma = Mapa.de; break;
      case "it": Idioma = Mapa.it; break;
      case "fr": Idioma = Mapa.fr; break;
      case "pt": Idioma = Mapa.pt; break;
    }

    // if (confirm("Següent?") == true) 
    for (i in Idioma) {
        document.getElementById(i).title = + (parseInt(i) + 1) + ". " + Idioma[i];
        // Finestra(i);
        // Bafarada(i);
    }

    // window.alert("scrollTo(0, 200)");
    window.scrollTo(0, 900);
    document.getElementById("0.").focus();
}

function Bafarada(IdText, X = 200, Y = 200)
{
    // window.alert((parseInt(IdText) + 1) + ". " + Idioma[IdText]);
    /*
    document.getElementById("miniature-popup").style.display = "block";
    document.getElementById("miniature-popup").innerHTML = 
            (parseInt(IdText) + 1) + ". " + Idioma[IdText];
    */
   
    // window.alert("scrollTo("+ X +", "+ Y +")");
    window.scrollTo(X, Y);
    Finestra(IdText, X, Y);
}

function Finestra(IdText, X = 200, Y = 200)
{
    // window.alert(", height=" + (Idioma[IdText].length / 4) + ", left=" + X + ", top="  + Y);
    /*
    window.alert("<a href='https://translate.google.com/?hl=" + 
        IdIdioma + "&sl=" + IdIdioma + "&tl=" + IdIdioma + "&text=" + 
        Idioma[IdText] + "&op=translate'><img src='img/on.png' alt='on'/></a>");
    */       
    const myWindow = window.open("", "_blank", "width=800" + 
        ", height=" + (Idioma[IdText].length / 4) + 
        ", left=" + X + // (10 + IdText * 33) +
        ", top="  + Y + // (10 + IdText * 25) + 
        ", location=0, menubar=0, resizable=0" + 
        ", scrollbars=0, status=0, titlebar=0, toolbar=0");
    myWindow.document.open();
    myWindow.document.write('<html>' + 
        '<head><title>Breviculum: Il·lustracions. Textos</title> \n\
         <link rel="stylesheet" href="css/demo.css">' + 
        '<script>window.onload = function() { document.getElementById("close").focus(); }</script>' +    
        '</head><body onload="document.getElementById("close").focus();"><p>' + (parseInt(IdText) + 
        1) + '. ' + Idioma[IdText] + '<br>' + 
        /*
        '<a href="https://translate.google.com/?hl=' + 
        IdIdioma + '&sl=' + IdIdioma + '&tl=' + IdIdioma + '&text=' + 
        Idioma[IdText] + '&op=translate"><img src="img/on.png" alt="on"/></a>' +  
        */       
        '<button id="close" onclick="window.close()"><img src="img/close.png" alt="close"/></button>' +
        /*
        '<br><button type="button" onclick="Bafarada(' + 
                parseInt(IdText) + ', ' + X + ',  ' + Y + ')">' + 
                parseInt(IdText) + '.</button>' +
            '<button type="button" onclick="Bafarada(' + 
               (parseInt(IdText) + 2) +  ', ' + X + ',  ' + Y + ')">' + 
               (parseInt(IdText) + 2) + '.</button>' +
        */       
        '</p></body></html>');
    myWindow.document.close();            
}

